const getRandomElement = (e) => {
    try {
      if (e instanceof Array == !1 || 0 === e.length)
        throw new Error(
          "Could not get a random element from an empty array or a non-array value"
        );
      const a = Math.floor(Math.random() * e.length);
      return e[a];
    } catch (e) {
      console.error(e);
    }
  },
  getCurrentPath = () => {
    try {
      const e = window.location.pathname;
      return "" === e ? "/" : e;
    } catch (e) {
      console.error(e);
    }
  },
  photos = {
    offer: [
      "images/nasza-oferta/DALL·E-2022-09-18-12.53.37.webp",
      "images/nasza-oferta/DALL·E-2022-09-18-12.53.44.webp",
      "images/nasza-oferta/DALL·E-2022-09-18-12.53.50.webp",
      "images/nasza-oferta/DALL·E-2022-09-18-12.53.56.webp",
    ],
    news: [
      "images/aktualnosci/DALL·E-2022-09-18-12.52.00.webp",
      "images/aktualnosci/DALL·E-2022-09-18-12.52.07.webp",
      "images/aktualnosci/DALL·E-2022-09-18-12.52.14.webp",
      "images/aktualnosci/DALL·E-2022-09-18-12.52.20.webp",
    ],
    about: [
      "images/o-nas/DALL·E-2022-09-29-19.30.41.webp",
      "images/o-nas/DALL·E-2022-09-29-19.30.47.webp",
      "images/o-nas/DALL·E-2022-09-29-19.30.52.webp",
    ],
  },
  heroImage = document.querySelector(".hero"),
  currentPath = getCurrentPath();
switch (currentPath) {
  case "/aktualnosci.html":
    heroImage.src = getRandomElement(photos.news);
    break;
  case "/o-nas.html":
    heroImage.src = getRandomElement(photos.about);
    break;
  case "/nasza-oferta.html":
    heroImage.src = getRandomElement(photos.offer);
}
